# atom.sql was originally generated by the autoSql program, which also 
# generated atom.c and atom.h.  This creates the database representation of
# an object which can be loaded and saved from RAM in a fairly 
# automatic way.

#Table of atoms in more than one species
CREATE TABLE atom (
    name varchar(255) not null,	# atom name
    instance int unsigned not null,	# instance number
    species varchar(255) not null,	# species name
    chrom varchar(255) not null,	# chromosome
    start int unsigned not null,	# chrom start
    end int unsigned not null,	# chrom end
    strand char(1) not null	# + or -
              #Indices
);
