#Mapping table for tblastn protein tracks
CREATE TABLE blastRef (
    acc varchar(255) not null,	# accession
    geneId varchar(255) not null,	# gene name
    refPos varchar(255) not null,	# Reference Position
    extra1 varchar(255) ,	# other link 1 (e.g. SwissProt name)
    extra2 varchar(255) ,	# other link 2
              #Indices
    PRIMARY KEY(acc),
    INDEX(geneId(16)),
    INDEX(extra1(16))
);
