# ccdsInfo.sql was originally generated by the autoSql program, which also 
# generated ccdsInfo.c and ccdsInfo.h.  This creates the database representation of
# an object which can be loaded and saved from RAM in a fairly 
# automatic way.

#Consensus CDS information, links CCDS ids to NCBI and Hinxton accessions
CREATE TABLE ccdsInfo (
    ccds char(12) not null,	# CCDS id
    srcDb char(1) not null,	# source database: N=NCBI, H=Hinxton
    mrnaAcc char(18) not null,	# mRNA accession (NCBI or Hinxton)
    protAcc char(18) not null,	# protein accession (NCBI or Hinxton)
              #Indices
    INDEX(ccds),
    INDEX(mrnaAcc)
);
