# generated cdsEvidence.c and cdsEvidence.h.  This creates the database representation of
# an object which can be loaded and saved from RAM in a fairly 
# automatic way.

#Evidence for CDS boundary within a transcript
CREATE TABLE cdsEvidence (
    name varchar(255) not null,	# Name of transcript
    start int not null,	# CDS start within transcript, zero based
    end int not null,	# CDS end, non-inclusive
    source varchar(255) not null,	# Source of evidence
    accession varchar(255) not null,	# Genbank/uniProt accession
    score double not null,	# 0-1000, higher is better
    startComplete tinyint unsigned not null,	# Starts with ATG?
    endComplete tinyint unsigned not null,	# Ends with stop codon?
    cdsCount int not null,	# Number of CDS blocks
    cdsStarts longblob not null,	# Start positions of CDS blocks
    cdsSizes longblob not null,	# Sizes of CDS blocks
              #Indices
    PRIMARY KEY(name)
);
