# lsSnpPdb.sql was originally generated by the autoSql program, which also 
# generated lsSnpPdb.c and lsSnpPdb.h.  This creates the database representation of
# an object which can be loaded and saved from RAM in a fairly 
# automatic way.

#Mapping of SNPs to protein structures obtained from LS-SNP PDB
CREATE TABLE lsSnpPdb (
    protId varchar(255) not null,	# Protein Id
    pdbId varchar(255) not null,	# PDB id
    structType enum("XRay", "NMR") not null,	# type of structure
    chain char(1) not null,	# PDB chain id
    snpId varchar(255) not null,	# dbSNP id
    snpPdbLoc int not null,	# location of SNP in PDB chain (1-based AA index)
              #Indices
    PRIMARY KEY(protId)
);
