#All rna Genes
CREATE TABLE rnaGenes (
    bin int unsigned not null,
    chrom varchar(255) not null,	# Human chromosome or FPC contig
    chromStart int unsigned not null,	# Start position in chromosome
    chromEnd int unsigned not null,	# End position in chromosome
    name varchar(255) not null,	# Other chromosome involved
    score int unsigned not null,	# Score from 900-1000.  1000 is best
    strand char(1) not null,	# Value should be + or -
    aa varchar(255) not null,	# Amino acid for the tRNA
    ac varchar(255) not null,	# Anticodon for the tRNA
    ci char(1) not null,	# Contains intron or not: Y or N
    scan float not null,	# tRNAScanSE score
              #Indices
    PRIMARY KEY(name)
);
