#data related to sage Experiments, tissued descriptions, etc.
CREATE TABLE sageExp (
    num int not null,	# Index of the experiment in the sage table.
    exp varchar(255) not null,	# experiments name.
    totalCount int not null,	# Sum of all the tag counts for this experiment.
    tissueType varchar(255) not null,	# Brief Description of cells used.
    tissueDesc varchar(255) not null,	# Brief Description of tissues.
    tissueSupplier varchar(255) not null,	# Who supplied the tissue.
    organism varchar(255) not null,	# organism source of cells.
    organ varchar(255) not null,	# organ identifier.
    producer longblob not null,	# source of tissue.
    desription longblob not null,	# description of experiment.
              #Indices
    PRIMARY KEY(num)
);
