# sgdClone.sql was originally generated by the autoSql program, which also 
# generated sgdClone.c and sgdClone.h.  This creates the database representation of
# an object which can be loaded and saved from RAM in a fairly 
# automatic way.

#Clone info from yeast genome database
CREATE TABLE sgdClone (
    bin smallint unsigned not null,   # Bin for fast index
    chrom varchar(255) not null,	# Chromosome in chrN format
    chromStart int unsigned not null,	# Start position in chromosome
    chromEnd int unsigned not null,	# End position in chromosome
    name varchar(255) not null,	# Washington University Name
    atccName varchar(255) not null,	# ATCC clone name (optional)
              #Indices
    INDEX(chrom(8),bin),
    INDEX(name(8))
);
