#Softberry's Promoter Predictions
CREATE TABLE softPromoter (
    chrom varchar(255) not null,	# Human chromosome or FPC contig
    chromStart int unsigned not null,	# Start position in chromosome
    chromEnd int unsigned not null,	# End position in chromosome
    name varchar(255) not null,	# As displayed in browser
    score int unsigned not null,	# Score from 0 to 1000
    type varchar(255) not null,	# TATA+ or TATAless currently
    origScore float not null,	# Score in original file, not scaled
    origName varchar(255) not null,	# Name in original file
    blockString varchar(255) not null,	# From original file.  I'm not sure how to interpret.
              #Indices
    INDEX(chrom(12),chromStart),
    INDEX(name(12))
);
