#Synteny map from one genome to another
CREATE TABLE synMap (
    chromA varchar(255) not null,	# chromosome in organism A
    chromStartA int unsigned not null,	# chromosome start in orgainsm A
    chromEndA int unsigned not null,	# chromosome end in orgainsm A
    strandA varchar(255) not null,	# Strand information can be things like '-+'
    chromB varchar(255) not null,	# chromosome in organism B
    chromStartB int unsigned not null,	# chromosome start in orgainsm B
    chromEndB int unsigned not null,	# chromosome end in orgainsm B
              #Indices
    PRIMARY KEY(chromA)
);
