#shared about transMap source alignments
CREATE TABLE transMapSrc (
    db char(16) not null,	# source db
    id varchar(255) not null,	# id of source alignment
    chrom varchar(255) not null,	# chromosome
    chromStart int unsigned not null,	# chrom start location
    chromEnd int unsigned not null,	# chrom end location
    strand char(1) not null,	# strand
    ident float not null,	# identity
    aligned float not null,	# fraction of sequence aligned
              #Indices
    PRIMARY KEY(db,id)
);
