#A cluster of transcripts or coding regions
CREATE TABLE txCluster (
    chrom varchar(255) not null,	# Chromosome (or contig)
    chromStart int not null,	# Zero based start within chromosome.
    chromEnd int not null,	# End coordinate, non inclusive.
    name varchar(255) not null,	# Cluster name
    score int not null,	# BED score - 0-1000.  0 if not used.
    strand char(1) not null,	# Strand - either plus or minus
    txCount int not null,	# Count of transcripts
    txArray longblob not null,	# Array of transcripts
              #Indices
    PRIMARY KEY(chrom)
);
